	public boolean fillChests(World world, Random rand, BlockPos position) {
		int x = position.getX();
		int y = position.getY();
		int z = position.getZ();
		TileEntityChest chest = (TileEntityChest) world.getTileEntity(new BlockPos(new BlockPos(x + 18, y + 0, z + 13)));

		if (chest != null) {
			for (int i = 0; i < chest.getSizeInventory(); i++) {
				chest.setInventorySlotContents(i, null);
			}

			chest.setLootTable(LootTableList.CHESTS_SIMPLE_DUNGEON, rand.nextLong());
		}

		chest = (TileEntityChest) world.getTileEntity(new BlockPos(x + 6, y + 0, z + 13));

		if (chest != null) {
			for (int i = 0; i < chest.getSizeInventory(); i++) {
				chest.setInventorySlotContents(i, null);
			}

			chest.setLootTable(LootTableList.CHESTS_SIMPLE_DUNGEON, rand.nextLong());
		}
		return false;
	}
